import React, { useState } from "react";
import Rules from "./Rules";

function Nav() {
  const [showRules, setShowRules] = useState(false);

  const toggleRules = () => {
    setShowRules(!showRules);
  };

  return (
    <div className="pt-10 flex justify-between items-center relative">
      <h1 className="text-3xl font-bold underline absolute left-1/2 transform -translate-x-1/2">
        KDotle
      </h1>
      <div className="text-lg cursor-pointer" onClick={toggleRules}>
        Rules
      </div>
      {showRules && <Rules />} {/* Assuming Rules is a component to display rules */}
    </div>
  );
}

export default Nav;
