import React, { useState } from "react";
import Rules from "./Rules";

function Nav() {
  const [showRules, setShowRules] = useState(false);

  const toggleRules = () => {
    setShowRules(!showRules);
  };

  return (
    <div className="flex justify-between items-center relative">
      <div className="text-3xl font-bold underline"> {/* Centered title */}
        KDotle
      </div>
      <div className="text-lg cursor-pointer" onClick={toggleRules}>
        Rules
      </div>
      {showRules && <Rules />} {/* Display Rules component conditionally */}
    </div>
  );
}

export default Nav;
